// 지도 중심좌표(pos)와 지역명을 받아 위치를 보정하는 함수
function getLabelPosition(
  region: { name: string },
  pos: { x: number; y: number }
) {
  let { x, y } = pos;
  switch (region.name) {
    case "세종":
      x += 18;
      y -= 10;
      break;
    case "충청남도":
      x -= 18;
      y += 10;
      break;
    case "대전":
      y += 18;
      break;
    // 필요시 다른 지역도 추가
    default:
      break;
  }
  return { x, y };
}

const KoreaMap: React.FC<KoreaMapProps> = ({ onRegionClick, places }) => {
  const placeCounts = useMemo(() => {
    const counts: { [key: string]: number } = {};
    regions.forEach((r) => (counts[r.name] = 0));
    places.forEach((place) => {
      const regionName = Object.keys(regionNameMapping).find((name) =>
        place.address.includes(name)
      );
      if (regionName) {
        counts[regionName] = (counts[regionName] || 0) + 1;
      }
    });
    return counts;
  }, [places]);

  return (
    <div className="w-full h-full flex items-center justify-center">
      <svg
        viewBox="0 0 1000 1000"
        width="1000"
        height="1000"
        className="w-full h-full"
        fill="#e5e7eb" // gray-300
        stroke="#ffffff"
        strokeWidth={0.5}
        strokeLinecap="round"
        strokeLinejoin="round"
      >
        <g>
          {regions.map((region) => (
            <path
              key={region.id}
              d={region.path}
              className="cursor-pointer transition-colors stroke-white stroke-1 fill-gray-300 hover:fill-blue-400"
              onClick={() => onRegionClick(region.name)}
            />
          ))}
        </g>

        <g>
          {regions.map((region) => {
            const count = placeCounts[region.name] || 0;
            if (!count) return null;
            const pos = regionLabelPositions[region.name];
            if (!pos) return null;
            const { x, y } = getLabelPosition(region, pos);
            return (
              <React.Fragment key={region.id}>
                <text
                  x={x}
                  y={y}
                  textAnchor="middle"
                  alignmentBaseline="middle"
                  fontSize={18}
                  fontWeight={900}
                  fill="#222"
                  stroke="#fff"
                  strokeWidth={4}
                  paintOrder="stroke"
                  pointerEvents="none"
                  style={{ userSelect: "none" }}
                >
                  {`${region.name} (${count})`}
                </text>
                <text
                  x={x}
                  y={y}
                  textAnchor="middle"
                  alignmentBaseline="middle"
                  fontSize={18}
                  fontWeight={900}
                  fill="#222"
                  pointerEvents="none"
                  style={{ userSelect: "none" }}
                >
                  {`${region.name} (${count})`}
                </text>
              </React.Fragment>
            );
          })}
        </g>
      </svg>
    </div>
  );
};

export default KoreaMap;
