// 지도 중심좌표(pos)와 지역명을 받아 위치를 보정하는 함수
function getLabelPosition(
  region: { name: string },
  pos: { x: number; y: number }
) {
  let { x, y } = pos;
  switch (region.name) {
    case "세종":
      x += -15;
      y -= 16;
      break;
    case "충청남도":
      x -= 18;
      y += 10;
      break;
    case "대전":
      y += 45;
      break;
    case "부산":
      x += 20;
      y += 25;
      break;
    case "울산":
      x += 30;
      y += 5;
      break;
    case "대구":
      x += 30;
      y += -5;
      break;
    // 필요시 다른 지역도 추가
  }
  return { x, y };
}

